type ThemeType = "actom-dark" | "cb" | "coldark-dark" | "dark" | "holi-theme" | "duotone-earth" | "duotone-forest" | "duotone-light" | "duotone-sea" | "duotone-space" | "funky" | "ghcolors" | "gruvbox-light" | "laserwave" | "lucario" | "night-owl" | "okaidia" | "one-dark" | "one-light" | "solarized-dark-atom" | "synthwave84" | "tomorrow" | "twilight" | "vs" | "vsc-dark-plus" | "z-touch";
interface ShowdownPrismOptions {
    languages?: string[];
    theme?: ThemeType;
}
/**
 * A Showdown extension for Prism.js.
 * ---
 * This extension adds syntax highlighting to code blocks in markdown documents
 * using Prism.js. This extension is meant to be used with the Showdown library.
 *
 *
 * **Options**
 *
 * **1.languages ? :  string[ ]**
 *
   - Prismjs will load the default languages: markup, css, clike and javascript.

   - You can load more languages with the `options.languages`.
        If you use Webpack or another bundler , do not set `options.languages`.
 *
 * **2.theme ? : string**
 *
 * - Available themes can be see in README.md


 * @example
 *
 * import showdown from "showdown";
        import showdownprism from "showdown-prism";
        // Add extension to showdown options
        const converter = new showdown.Converter({
        extensions: [showdownPrism({
                    languages: ["bash"],
                    theme: "holi-theme",
                }),],
        });
        // Convert to HTML
        const convertedContent = converter.makeHtml("Markdown_contents")
 */
declare const showdownPrism: (options?: ShowdownPrismOptions) => ShowdownExtension | ShowdownExtension[];

export { type ShowdownPrismOptions, type ThemeType, showdownPrism as default };
